if(document.getElementsByClassName("slider").length){new Swiper(".slider",{slideClass:"slider-item",slidesPerView:1,loop:!0,pagination:{el:".slider-bullets",clickable:!0,bulletClass:"bullet"},navigation:{nextEl:".slider .arrow-right",prevEl:".slider .arrow-left"}})}if(document.getElementsByClassName("news-carousel").length){new Swiper(".news-carousel",{slidesPerView:3,spaceBetween:20,slideClass:"news-item",setWrapperSize:!0,breakpoints:{300:{slidesPerView:1,spaceBetween:10},540:{slidesPerView:2,spaceBetween:10},720:{slidesPerView:3,spaceBetween:10},1000:{slidesPerView:3,spaceBetween:15},1400:{slidesPerView:4,spaceBetween:20},3200:{slidesPerView:4,spaceBetween:20}}})}if(document.getElementsByClassName("reviews-carousel").length){new Swiper(".reviews-carousel",{slideClass:"reviews-item",slidesPerView:1,pagination:{el:".reviews-bullets",clickable:!0,bulletClass:"bullet"},navigation:{nextEl:".reviews .arrow-right",prevEl:".reviews .arrow-left"}})}document.querySelectorAll("main").forEach(e=>{const s=e.querySelectorAll(".filter .item"),l=e.querySelectorAll(".catalog-main .catalog");let i;s.forEach(e=>{e.addEventListener("click",()=>{s.forEach(e=>{e.classList.remove("active")}),e.classList.add("active"),i=e.getAttribute("data-tab-index"),l.forEach(e=>{e.classList.contains(i)?e.classList.add("active"):e.classList.remove("active")})})})});