"use strict";document.querySelectorAll("main").forEach(e=>{const t=e.querySelectorAll(".fermer-tabs .item"),a=e.querySelectorAll(".fermer-tab");let c;t.forEach(e=>{e.addEventListener("click",()=>{t.forEach(e=>{e.classList.remove("active")}),e.classList.add("active"),c=e.getAttribute("data-tab-index"),a.forEach(e=>{e.classList.contains(c)?e.classList.add("active"):e.classList.remove("active")})})})});